catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   if (newValue =="Ticket Issue")
   {

		g_form.clearOptions('subcategory');
		g_form.addOption('subcategory','Refund Issue','Refund Issue');
		g_form.addOption('subcategory','Reservation Issue','Reservation Issue');
 }

if (newValue =="Catering Issue")
   {

		g_form.clearOptions('subcategory');
		g_form.addOption('subcategory','Food Quality not good','Food Quality not good');
		g_form.addOption('subcategory','Food Not Provided','Food Not Provided');
 }
   if (newValue =="Berth Issue")
   {

		g_form.clearOptions('subcategory');
		g_form.addOption('subcategory','Blanket not available','Blanket not available');
		g_form.addOption('subcategory','AC not working','AC not working');
		g_form.addOption('subcategory','Light Issue','Light Issue');
 }

}
