sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Get the 'subcategory' field value
    var subcategory = g_form.getValue('subcategory');

    // Set priority field (assuming it's called 'u_priority') to P1 if subcategory is 'refund_issue'
    if (subcategory === 'Refund Issue') {
        g_form.setValue('priorit0', 'P1');
    } else if (subcategory === 'Reservation Issue') {
        g_form.setValue('priorit0', 'P2');
    } else if (subcategory === 'Food Quality not good') {
        g_form.setValue('priorit0', 'P1');
    } else if (subcategory === 'Food Not Provided') {
        g_form.setValue('priorit0', 'P2');
    } else if (subcategory === 'Blanket not available') {
        g_form.setValue('priorit0', 'P2');
    } else if (subcategory === 'AC not working') {
        g_form.setValue('priorit0', 'P1');
    } else if (subcategory === 'Light Issue') {
        g_form.setValue('priorit0', 'P2');
    } else {
        // Clear the priority field if the subcategory is no longer 'refund_issue'
        g_form.setValue('priorit0', '');
    }
}
